document.addEventListener("DOMContentLoaded",()=>{const t=document.getElementById("mobile-menu-button"),o=document.getElementById("mobile-menu"),n=document.getElementById("main-nav"),d=document.querySelector(".mobile-dropdown-toggle"),s=document.querySelector(".mobile-dropdown-content"),i=document.querySelector(".dropdown-icon");t&&o&&t.addEventListener("click",()=>{o.classList.toggle("hidden");const e=t.getAttribute("aria-expanded")==="true";t.setAttribute("aria-expanded",!e)}),window.addEventListener("scroll",()=>{window.scrollY>20?n.classList.add("shadow-md"):n.classList.remove("shadow-md")}),d&&s&&i&&d.addEventListener("click",e=>{e.preventDefault(),s.classList.toggle("hidden"),i.classList.toggle("rotate-180")}),document.querySelectorAll(".mobile-nav-link").forEach(e=>{e.addEventListener("click",()=>{!e.closest(".mobile-dropdown-toggle")&&!e.classList.contains("mobile-dropdown")&&setTimeout(()=>{o.classList.add("hidden"),t.setAttribute("aria-expanded","false")},150)})})});
